import axios from 'axios';

// הגדרת כתובת ה-API כברירת מחדל
axios.defaults.baseURL = "https://localhost:5195";
//const  apiUrl= "https://localhost:5195";
// יצירת interceptor לטיפול בשגיאות
const errorInterceptor = (error) => {
  // רישום הודעת השגיאה ללוג
  console.error('Axios Error:', error.message);

  // החזרת התשובה המקורית (לצורך ניתוח נוסף)
  return Promise.reject(error);
};

// הוספת interceptor לכל בקשות axios
axios.interceptors.response.use(undefined, errorInterceptor);

export default {
  getTasks: async () => {
    try {
      const result = await axios.get(`/items`);
      return result.data;
    } catch (error) {
      console.error('getTasks failed:', error.message);
      throw error;
    }
  },

  addTask: async (name) => {
    try {
      console.log('addTask', name);
      const result = await axios.post(`/tasks`, { taskName: name });
      return result.data;
    } catch (error) {
      console.error('addTask failed:', error.message);
      throw error;
    }
  },

  setCompleted: async (id, isComplete) => {
    try {
      console.log('setCompleted', { id, isComplete });
      const result = await axios.put(`${apiUrl}/tasks/${id}`, { IsComplete: isComplete });
      return result.data;
    } catch (error) {
      console.error('setCompleted failed:', error.message);
      throw error;
    }
  },

  deleteTask: async (id) => {
    try {
      console.log('deleteTask');
      const result = await axios.delete(`${apiUrl}/tasks/${id}`);
      return result.data;
    } catch (error) {
      console.error('deleteTask failed:', error.message);
      throw error;
    }
  },
};
